// Lazy Load example using a loop assuming the session is still open\nforeach (var message in mainMessages)\n{\n    ISet<Message> answers = message.Answers; // this will trigger a query to load replies if not already loaded\n}\n\n// Or use HQL/QueryOver for explicit fetching of replies\nIList<Message> messagesWithReplies = session.CreateCriteria<Message>()\n    .Add(Restrictions.IsNull("ParentId"))\n    .SetFetchMode("Answers", FetchMode.Join) // Use Join or Select mode based on your needs\n    .List<Message>();