using System.IO;\nusing iTextSharp.text.pdf;\n\npublic byte[] AppendPdfs(byte[] firstPdfBytes, byte[] secondPdfBytes)\n{\n    using (var outputStream = new MemoryStream())\n    {\n        PdfCopy pdfCopy = new PdfCopy(new PdfDocument(new PdfReader(firstPdfBytes)), outputStream);\n\n        // Add pages from the first document\n        using (PdfDocument pdfDoc1 = new PdfDocument(new PdfReader(firstPdfBytes)))\n        {\n            for (int i = 1; i <= pdfDoc1.NumberOfPages; i++)\n            {\n                pdfCopy.AddPage(pdfCopy.GetImportedPage(pdfDoc1, i));\n            }\n        }\n\n        // Add pages from the second document\n        using (PdfDocument pdfDoc2 = new PdfDocument(new PdfReader(secondPdfBytes)))\n        {\n            for (int i = 1; i <= pdfDoc2.NumberOfPages; i++)\n            {\n                pdfCopy.AddPage(pdfCopy.GetImportedPage(pdfDoc2, i));\n            }\n        }\n\n        // Close the copy object and return the PDF bytes\n        pdfCopy.Close();\n        \n        return outputStream.ToArray();\n    }\n}