using System.IO;\nusing iTextSharp.text.pdf;\n\npublic void AppendPdfs(byte[] firstPdfBytes, byte[] secondPdfBytes, string outputFilePath)\n{\n    using (FileStream outputStream = new FileStream(outputFilePath, FileMode.Create, FileAccess.Write))\n    {\n        PdfCopy pdfCopy = new PdfCopy(new PdfDocument(new PdfReader(firstPdfBytes)), outputStream);\n        PdfDocument pdfDoc = new PdfDocument(new PdfReader(secondPdfBytes));\n\n        // Copy all pages from the first PDF\n        for (int i = 1; i <= pdfDoc.NumberOfPages; i++)\n        {\n            pdfCopy.AddPage(pdfCopy.GetImportedPage(pdfDoc, i));\n        }\n\n        pdfDoc.Close();\n        \n        // Now add all pages from the second PDF\n        pdfDoc = new PdfDocument(new PdfReader(secondPdfBytes));\n        for (int i = 1; i <= pdfDoc.NumberOfPages; i++)\n        {\n            pdfCopy.AddPage(pdfCopy.GetImportedPage(pdfDoc, i));\n        }\n\n        pdfDoc.Close();\n    }\n}