using System;\nusing Cl.UPnP;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create a new Device\n        RootDevice device = DeviceFactory.CreateRootDevice(\n            "urn:schemas-upnp-org:device:MediaServer:1",\n            "uuid:SomeUniqueID",\n            "http://localhost:8080/device.xml");\n\n        // Add service to the device\n        Service actionHandler = ServiceFactory.CreateService(\n            "urn:schemas-upnp-org:service:ConnectionManager:1",\n            "urn:upnp-org:serviceId:ConnectionManager",\n            new MyActionHandler());\n\n        device.GetRootDevice().AddService(actionHandler);\n\n        // Start the HTTP server to serve UPnP descriptions\n        new HttpServer(8080).Start();\n\n        Console.WriteLine("UPnP Server started. Press any key to stop...");\n        Console.ReadKey();\n    }\n}\n\nclass MyActionHandler : ServiceActionHandler\n{\n    public override void HandleAction(Service service, Action action)\n    {\n        // Implement your handlers here.\n        Console.WriteLine("Handling action: " + action.Name);\n    }\n}