using System;\nusing Microsoft.Office.Interop.Excel;\n\npublic class ExcelFooterExample\n{\n    public static void Main()\n    {\n        Application excelApp = new Application();\n        Workbook workbook = excelApp.Workbooks.Add(Type.Missing);\n        Worksheet worksheet = workbook.Sheets[1];\n\n        // Access the footer for the center section of the first page\n        HeaderFooter footer = worksheet.PageSetup.CenterFooter;\n\n        // Set the footer text and format it in bold\n        footer.Text = "&\"Arial,Bold\"Your Footer Text";\n\n        // Include page number in the footer\n        worksheet.PageSetup.CenterFooter = "Page &P of &N";\n\n        // Display Excel application\n        excelApp.Visible = true;\n    }\n}