using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        if (!Console.IsOutputRedirected)\n        {\n            // Show progress animation only if output is not redirected\n            for (int i = 0; i <= 100; i++)\n            {\n                Console.Write($"\rProcessing: {i}% [{GetSpinner(i)}]");\n                Thread.Sleep(100); // Simulate work with a delay\n            }\n            Console.WriteLine(); // Move to the next line after completion\n        }\n        else\n        {\n            // Just do the processing without showing progress\n            for (int i = 0; i <= 100; i++)\n            {\n                Thread.Sleep(100); // Simulate work with a delay\n            }\n            Console.WriteLine("Processing complete!");\n        }\n    }\n\n    static char GetSpinner(int step)\n    {\n        switch ((step / 25) % 4)\n        {\n            case 0: return '/';\n            case 1: return '-';\n            case 2: return '\\';\n            default: return '|';\n        }\n    }\n}