using System;\nusing System.Reflection;\n\npublic class FieldDescriptorExample\n{\n    public int[][][] ArrayField { get; set; }\n\n    public static void Main()\n    {\n        // Get the type information for the class\n        Type type = typeof(FieldDescriptorExample);\n\n        // Get the field information\n        FieldInfo fieldInfo = type.GetField("ArrayField", BindingFlags.Public | BindingFlags.Instance);\n\n        if (fieldInfo != null)\n        {\n            Console.WriteLine($"Field Name: {fieldInfo.Name}");\n            Console.WriteLine($"Field Type: {fieldInfo.FieldType}");\n\n            // If it's an array, you can get more details\n            Type fieldType = fieldInfo.FieldType;\n            while (fieldType.IsArray)\n            {\n                Console.WriteLine("Is Array Rank: " + fieldType.GetArrayRank());\n                fieldType = fieldType.GetElementType();\n            }\n\n            Console.WriteLine($"Underlying Element Type: {fieldType}");\n        }\n    }\n}