protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindUserData();\n    }\n}\n\nprivate void BindUserData()\n{\n    int userId = GetCurrentUser(); // Replace with method to get current user ID\n    using (var context = new MyDbContext())\n    {\n        var user = context.Users.FirstOrDefault(u => u.UserId == userId);\n        fvUserProfile.DataSource = new List<User> { user }; // Wrap in list for FormView\n        fvUserProfile.DataBind();\n    }\n}\n\nprotected void fvUserProfile_ItemUpdating(object sender, FormViewUpdateEventArgs e)\n{\n    int userId = GetCurrentUser(); // Replace with method to get current user ID\n    using (var context = new MyDbContext())\n    {\n        var user = context.Users.FirstOrDefault(u => u.UserId == userId);\n        if (user != null)\n        {\n            user.Name = ((TextBox)fvUserProfile.FindControl("tbName")).Text;\n            user.Email = ((TextBox)fvUserProfile.FindControl("tbEmail")).Text;\n\n            context.SaveChanges();\n        }\n    }\n\n    fvUserProfile.ChangeMode(FormViewMode.Readonly); // After update, change mode to Display\n    BindUserData(); // Rebind the data to reflect changes\n}\n\nprivate int GetCurrentUser()\n{\n    // Implement logic to retrieve current user ID from session or authentication ticket\n    return User.Identity.IsAuthenticated ? Convert.ToInt32(User.Identity.Name) : 0;\n}