var groupedData = from p in DtPoruchy.FindAll()\n                  join a in MtAuta.FindAll() on p.ID_Auta equals a.ID into autoGroup\n                  from a2 in autoGroup.DefaultIfEmpty()\n                  where p.SERVIS >= 3\n                  group new { p, a2 } by new { p.ID_Auta } into grouped\n                  select new \n                  {\n                      SumCena = grouped.Sum(x => x.p.CENA),\n                      CarId = grouped.Key.ID_Auta,\n                      MaxServis = grouped.Max(x => x.p.SERVIS)\n                  };