using System;\nusing System.IO;\n\npublic class ExifFinder\n{\n    public static void Main(string[] args)\n    {\n        string filePath = "example.jpg";\n        \n        using (FileStream stream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            byte[] soiMarker = { 0xFF, 0xD8 }; // Start of Image marker\n            byte[] exifMarker = { 0xFF, 0xE1 }; // EXIF marker\n\n            // Check if the file starts with SOI marker\n            byte[] buffer = new byte[2];\n            stream.Read(buffer, 0, 2);\n            if (!buffer.SequenceEqual(soiMarker))\n            {\n                Console.WriteLine("Not a valid JPEG file.");\n                return;\n            }\n\n            while (true)\n            {\n                stream.Read(buffer, 0, 2); // Read next marker\n                int segmentLength = (stream.ReadByte() << 8) | stream.ReadByte();\n\n                if (buffer.SequenceEqual(exifMarker))\n                {\n                    Console.WriteLine("EXIF found at position: " + stream.Position);\n                    // You can now read the EXIF data up to the length of the segment.\n                    break;\n                }\n\n                if (segmentLength > 0 && segmentLength <= stream.Length - stream.Position)\n                {\n                    stream.Seek(segmentLength - 4, SeekOrigin.Current); // Skip over this segment\n                }\n            }\n        }\n    }\n}