using System;\nusing System.Data.SqlClient;\nusing System.Web.Services;\n\npublic partial class Query : System.Web.UI.Page\n{\n    [WebMethod]\n    public static string GetData()\n    {\n        using (SqlConnection con = new SqlConnection(System.Configuration.ConfigurationManager.ConnectionStrings["YourConnectionString"].ConnectionString))\n        {\n            con.Open();\n            SqlCommand cmd = new SqlCommand("SELECT Data FROM YourTable", con);\n            SqlDataReader reader = cmd.ExecuteReader();\n            if (reader.Read())\n            {\n                return reader["Data"].ToString();\n            }\n            return "No data found";\n        }\n    }\n}