protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Ensure to convert to DateTime rather than string for the parameters\n        DateTime firstOfMonth = new DateTime(DateTime.Today.Year, DateTime.Today.Month, 1);\n        int daysInMonth = DateTime.DaysInMonth(firstOfMonth.Year, firstOfMonth.Month);\n        DateTime lastOfMonth = firstOfMonth.AddDays(daysInMonth - 1);\n\n        SqlDataSource1.SelectParameters["EventDate"].DefaultValue = firstOfMonth.ToString("yyyy-MM-dd");\n        SqlDataSource1.SelectParameters["EventDate2"].DefaultValue = lastOfMonth.ToString("yyyy-MM-dd");\n    }\n}