// Assume you have a class Person with properties Name, Age etc.\npublic class Person\n{\n    public string Name { get; set; }\n    public int Age { get; set; }\n    // other properties...\n}\n\n// Your DataGridView is populated from List<Person>\nList<Person> people = GetPeople(); // Method to populate your list of people\ndataGridView1.DataSource = people;\n\n// LINQ query to count rows where age is in specific range\nint variable1 = (from person in people\n                 where person.Age > 3 && person.Age < 5\n                 select person).Count();\n\nlabel1.Text = variable1.ToString();