// Example method to create a user and send a confirmation email\npublic void CreateUser(string username, string password, string email)\n{\n    MembershipCreateStatus status;\n    MembershipUser newUser = Membership.CreateUser(username, password, email, null, null, true, out status);\n\n    if (newUser != null && status == MembershipCreateStatus.Success)\n    {\n        // Generate verification URL\n        string verifyUrl = string.Format("{0}?ID={1}", "http://yourwebsite.com/Account/VerifyEmail.aspx", newUser.ProviderUserKey);\n        \n        // Create an email message with the link\n        MailMessage mail = new MailMessage();\n        mail.To.Add(newUser.Email);\n        mail.Subject = "Confirm your account";\n        mail.Body = $"Thank you for registering. Please click on the following link to verify your account: <a href='{verifyUrl}'>Verify Account</a>";\n        \n        // Send email (assuming SmtpClient is configured properly)\n        SmtpClient smtp = new SmtpClient();\n        smtp.Send(mail);\n    }\n}