using System;\nusing System.Data;\n\npublic class Example {\n    public static void Main() {\n        // Assuming ds is your DataSet and you have at least one DataTable\n        DataSet ds = new DataSet();\n        // Fill your DataSet here...\n        \n        // Let's assume the DataTable name is "YourTableName" and column name is "YourColumnName"\n        DataTable table = ds.Tables["YourTableName"];\n        string[] values = table.AsEnumerable()\n                              .Select(row => row.Field<string>("YourColumnName"))\n                              .ToArray();\n\n        // If you strictly need to avoid LINQ, here's a way using CopyTo:\n        object[] objValues = new object[table.Rows.Count];\n        for (int i = 0; i < table.Rows.Count; i++) {\n            objValues[i] = table.Rows[i]["YourColumnName"];\n        }\n        Array.Copy(objValues, values, objValues.Length);\n\n        // Now `values` array contains all the values of "YourColumnName"\n        \n        foreach(var value in values) {\n            Console.WriteLine(value);\n        }\n    }\n}