using System;\nusing System.Windows.Forms;\n\npublic enum VkKeyScanCode {\n    // Example keys, you can add more as needed\n    VK_BACK = 0x08,\n    VK_TAB = 0x09,\n    VK_CLEAR = 0x0C,\n    VK_RETURN = 0x0D,\n    VK_SHIFT = 0x10,\n    VK_CONTROL = 0x11,\n    VK_MENU = 0x12, // Alt key\n    // Add more keys as required\n}\n\npublic static class KeyConverter {\n    public static Keys VkKeyToSystemKeys(VkKeyScanCode vkKeyCode) {\n        return (Keys)vkKeyCode;\n    }\n}\n\nclass Program {\n    static void Main(string[] args) {\n        VkKeyScanCode vkKeyCode = VkKeyScanCode.VK_RETURN; // Example Virtual-Key Code\n        Keys systemKey = KeyConverter.VkKeyToSystemKeys(vkKeyCode);\n        Console.WriteLine($"Virtual-Key Code {vkKeyCode} is converted to System.Windows.Forms.Keys.{systemKey}");\n    }\n}