using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass SimpleWebCrawler\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public async Task<string> FetchPageAsync(string url)\n    {\n        try\n        {\n            HttpResponseMessage response = await client.GetAsync(url);\n            response.EnsureSuccessStatusCode();\n            string responseBody = await response.Content.ReadAsStringAsync();\n            return responseBody;\n        }\n        catch (HttpRequestException e)\n        {\n            Console.WriteLine($"Request error: {e.Message}");\n            return null;\n        }\n    }\n\n    static async Task Main(string[] args)\n    {\n        SimpleWebCrawler crawler = new SimpleWebCrawler();\n        string url = "https://example.com";\n\n        string pageContent = await crawler.FetchPageAsync(url);\n        \n        if (pageContent != null)\n        {\n            Console.WriteLine("Fetched HTML content:");\n            Console.WriteLine(pageContent.Substring(0, 500)); // Display the first 500 characters\n        }\n    }\n}