public static string FindTargetString(string response, int startPos, string startMatchString, string endMatchString)\n{\n    // Find the start position of the target string using IndexOf\n    int startIndex = response.IndexOf(startMatchString, startPos);\n    \n    if (startIndex == -1) return string.Empty; // Return empty if not found\n\n    // Calculate the actual content start index by adding the length of the start match string\n    int contentStartIndex = startIndex + startMatchString.Length;\n\n    // Find the end position of the target string\n    int endIndex = response.IndexOf(endMatchString, contentStartIndex);\n\n    if (endIndex == -1) return string.Empty; // Return empty if not found\n\n    // Extract and return the substring between the start and end positions\n    return response.Substring(contentStartIndex, endIndex - contentStartIndex);\n}