using System;\nusing Tweetinvi;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Authenticate with your Twitter credentials\n        Auth.SetUserCredentials("YourConsumerKey", "YourConsumerSecret", "YourAccessToken", "YourAccessTokenSecret");\n\n        // Fetch the latest tweets from a user timeline\n        var userTimeline = Timeline.GetUserTimeline(userScreenName: "twitteruser");\n        \n        foreach (var tweet in userTimeline)\n        {\n            Console.WriteLine($"{tweet.CreatedAt} - {tweet.Text}");\n        }\n    }\n}