using System;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        using (MailMessage mail = new MailMessage())\n        {\n            SmtpClient client = new SmtpClient("smtp.yourserver.com", 587) // Set your SMTP server and port here.\n            {\n                Credentials = new System.Net.NetworkCredential("username", "password"), // Set your credentials if needed\n                EnableSsl = true, // Use SSL if required\n            };\n\n            mail.From = new MailAddress("from@example.com");\n            \n            // No TO addresses are added\n            \n            // Add BCC recipients\n            mail.Bcc.Add(new MailAddress("bcc1@example.com"));\n            mail.Bcc.Add(new MailAddress("bcc2@example.com"));\n\n            mail.Subject = "Test Email";\n            mail.Body = "<h1>This is a test email sent via SMTP with only BCC addresses</h1>";\n            mail.IsBodyHtml = true;\n\n            client.Send(mail);\n        }\n    }\n}