using System;\n    using System.IO;\n    using System.Reflection;\n\n    class Program {\n        static void Main(string[] args) {\n            string pluginFolder = Directory.GetCurrentDirectory(); // or wherever your plugins are located\n\n            foreach (string file in Directory.GetFiles(pluginFolder, "*.dll")) {\n                Assembly assembly = Assembly.LoadFile(Path.GetFullPath(file));\n\n                foreach (Type type in assembly.GetTypes()) {\n                    if (!typeof(IPlugin).IsAssignableFrom(type) || type.IsInterface)\n                        continue;\n\n                    IPlugin pluginInstance = Activator.CreateInstance(type) as IPlugin;\n                    pluginInstance.Execute();\n                }\n            }\n        }\n    }