public class CustomAuthorizationPolicy : IAuthorizationPolicy\n   {\n       string id = Guid.NewGuid().ToString();\n\n       public bool Evaluate(EvaluationContext evaluationContext, ref object state)\n       {\n           // Implementation of method that examines claims\n           var isAuthorized = false;\n\n           return isAuthorized;\n       }\n\n       public ClaimSet Issuer => ClaimSet.System;\n\n       public ClaimSet OriginalIssuer => ClaimSet.System;\n\n       public string Id => id;\n\n       public ClaimSet IssuerClaims => new DefaultClaimSet(new Claim(ClaimTypes.AuthorizationDecision, "Allow"));\n   }