public class CustomSecurityTokenValidator : SecurityTokenValidator\n   {\n       public override bool CanValidateToken(SecurityToken token)\n       {\n           return (token is GenericXmlSecurityToken);\n       }\n\n       public override ClaimsIdentity ValidateToken(SecurityToken token, TokenValidationParameters validationParameters, out SecurityToken validatedToken)\n       {\n           validatedToken = token;\n\n           // Logic to validate your API key here\n           var claims = new List<Claim>;\n           claims.Add(new Claim(ClaimTypes.Name, "Username"));\n           return new ClaimsIdentity(claims);\n       }\n   }