using System;\nusing System.IO;\n\npublic class DirectoryCheck {\n    public void CheckAppGlobalResources(string path) {\n        string globalResourcesPath = Path.Combine(path, "App_GlobalResources");\n        if (Directory.Exists(globalResourcesPath)) {\n            Console.WriteLine("App_GlobalResources directory exists.");\n        } else {\n            Console.WriteLine("App_GlobalResources directory does not exist.");\n        }\n    }\n}