public class MainWindowViewModel : INotifyPropertyChanged\n{\n    public ObservableCollection<DiceViewModel> Dices { get; set; } = new ObservableCollection<DiceViewModel>();\n\n    private readonly ICommand _rollAllCommand;\n\n    public MainWindowViewModel()\n    {\n        // Initialize collection with DiceViewModels\n        for (int i = 0; i < 5; i++)\n        {\n            Dices.Add(new DiceViewModel());\n        }\n\n        _rollAllCommand = new RelayCommand(RollAll);\n    }\n\n    public ICommand RollAllCommand => _rollAllCommand;\n\n    private void RollAll()\n    {\n        foreach (var dice in Dices)\n        {\n            dice.Roll();\n        }\n    }\n}