// ExampleCSharp.cs (C#)\nusing System.Runtime.InteropServices;\n\npublic class MyUnmanagedCodeCaller\n{\n    // Import the DLL method\n    [DllImport("ExampleCPlusPlus.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern int Add(int a, int b);\n\n    public int UseAddFunction()\n    {\n        // Call the unmanaged function\n        return Add(5, 3);\n    }\n}