using Microsoft.Build.Evaluation;\nusing System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Path to your .csproj file.\n        string projectFilePath = @"C:\path\to\your\project.csproj";\n\n        var project = new Project(projectFilePath);\n\n        bool isUpToDate = project.Build("Build", null, out _);\n        \n        if (isUpToDate)\n        {\n            Console.WriteLine("The project is up-to-date.");\n        }\n        else\n        {\n            Console.WriteLine("The project needs to be rebuilt.");\n        }\n    }\n}