protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if(FileUploadControl.HasFile)\n    {\n        string fileName = FileUploadControl.FileName;\n        \n        // You can't get the full path for security reasons, but you have the filename.\n        StatusLabel.Text = "File name: " + fileName;\n        \n        // If needed, you could simulate a server-side path\n        string simulatedServerPath = Server.MapPath("~/UploadedFiles/" + fileName);\n        StatusLabel.Text += "<br>Simulated Server Path: " + simulatedServerPath;\n    }\n    else\n    {\n        StatusLabel.Text = "Please select a file to upload.";\n    }\n}