using System;\nusing System.IO;\n\npublic class AssemblyPathFinder\n{\n    public static string GetAssemblyLocation(string assemblyName)\n    {\n        try\n        {\n            var assembly = AppDomain.CurrentDomain.GetAssemblies()\n                .FirstOrDefault(a => a.FullName.Contains(assemblyName));\n            if (assembly != null)\n            {\n                // For GAC assemblies, this might not return a physical path in debug/release folders.\n                return assembly.Location;\n            }\n            else\n            {\n                return "Assembly not found.";\n            }\n        }\n        catch (Exception ex)\n        {\n            return $"Error: {ex.Message}";\n        }\n    }\n\n    public static void Main()\n    {\n        string assemblyName = "YourAssemblyName";\n        string path = GetAssemblyLocation(assemblyName);\n        Console.WriteLine($"Path of '{assemblyName}' is: {path}");\n    }\n}