using System;\nusing System.Web.Services;\nusing System.Web;\n\n[WebService(Namespace = "http://yoursite.com/")]\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\npublic class HiddenService : System.Web.Services.WebService\n{\n    [WebMethod]\n    public void DeleteItem(int itemId)\n    {\n        // Check if the request is from the same domain\n        string origin = HttpContext.Current.Request.Headers["Origin"];\n        if (string.IsNullOrEmpty(origin) || !origin.Contains("http://yoursite.com"))\n        {\n            throw new Exception("Cross-domain requests are not allowed.");\n        }\n\n        // Proceed with deletion logic here\n    }\n}