using System;\nusing System.Collections;\nusing System.DirectoryServices;\n\npublic void DisplayValue(DirectoryEntry de)\n{\n    if (de.Children != null)\n    {\n        foreach (DirectoryEntry child in de.Children)\n        {\n            PropertyCollection pc = child.Properties;\n            foreach (string propertyName in pc.PropertyNames)\n            {\n                PropertyValueCollection pvc = pc[propertyName];\n                Console.WriteLine($"Name: {propertyName}");\n                \n                foreach (object value in pvc)\n                {\n                    Console.WriteLine($"Value: {value}");\n                }\n                Console.WriteLine(); // For better readability between properties\n            }\n        }     \n    }  \n}