// Assuming you are setting up a request validation rule dynamically.\npublic void ValidateResponse()\n{\n    // Create a new instance of your validation rule type (e.g., FindTextValidationRule).\n    FindTextValidationRule validationRule = new FindTextValidationRule();\n\n    // Bind the find text from data source context.\n    validationRule.FindText = this.Context["UsersDataSource.Users#csv.?UserName"].ToString(); \n\n    // Set other properties as necessary, such as whether to ignore case.\n    validationRule.IgnoreCase = true;\n    \n    // Add rule to request\n    this.ValidateResponseUrl += new EventHandler<ValidateResponseEventArgs>( (sender, e) =>\n        {\n            e.ValidationRules.Add(validationRule);\n        });\n}