using System;\nusing System.Net;\nusing DotNetOpenAuth.Messaging;\n\npublic class ProxyHttpClient : IHttpClient {\n    private readonly WebProxy _proxy;\n\n    public ProxyHttpClient(string proxyUrl) {\n        _proxy = new WebProxy(proxyUrl);\n    }\n\n    public HttpWebResponse Send(HttpWebRequest request) {\n        request.Proxy = _proxy; // Set the proxy for this specific request.\n        return (HttpWebResponse)request.GetResponse();\n    }\n}