using System;\nusing System.Collections.Generic;\nusing LibPostal.Structs;\n\nnamespace AddressParser\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Initialize libpostal\n            AddressSetup.SetupDataDir("/path/to/libpostal-data");\n            \n            var addressParser = new LibPostal.Parser();\n\n            // Example text to parse\n            string textToParse = "23 Street n. 213 new york ny";\n\n            // Parse the address\n            ExpandedAddress parsedAddress = addressParser.Parse(textToParse);\n\n            // Output the results\n            foreach (var pair in parsedAddress.Components)\n            {\n                Console.WriteLine($"{pair.Key}: {pair.Value}");\n            }\n\n            // Teardown libpostal to free resources\n            AddressSetup.Teardown();\n        }\n    }\n}