// Assuming 'videoGrabber' is an instance of your VideoGrabber class\n\n// Set output format to Windows Media Format\nvideoGrabber.OutputFormat = TVideoOutputFormats.voWindowsMedia;\n\n// Set video and audio compression properties\nvideoGrabber.VideoCompressorGUID = TVideoCapture.VIDEOCOMPRESSION_WMV;\nvideoGrabber.AudioCompressorGUID = TVideoCapture.AUDIOCOMPRESSION_WMA;\n\n// Optionally, set the target file path\nvideoGrabber.TargetFile = "output.wmv";\n\n// Start capturing\nvideoGrabber.Start();