using System;\n   using System.IO;\n   using System.Configuration.Install;\n\n   [RunInstaller(true)]\n   public partial class CustomActions : Installer\n   {\n       private string ABCPath = @"C:\Path\To\Your\Folder\ABC";\n\n       protected override void OnBeforeUninstall(System.Collections.IDictionary savedState)\n       {\n           base.OnBeforeUninstall(savedState);\n           try\n           {\n               if (Directory.Exists(ABCPath))\n               {\n                   Directory.Delete(ABCPath, true); // true allows deletion of non-empty directory\n               }\n           }\n           catch (Exception ex)\n           {\n               // Log the exception or handle as appropriate\n               Console.WriteLine("Error: " + ex.Message);\n           }\n       }\n   }