using System;\nusing System.IO;\nusing System.Reflection;\n\npublic class SqlScriptLoader\n{\n    public static string GetSqlScript(string resourceName)\n    {\n        try\n        {\n            var assembly = Assembly.GetExecutingAssembly();\n            using (var stream = assembly.GetManifestResourceStream(resourceName))\n            {\n                if (stream == null) throw new ArgumentException("Could not find the resource", nameof(resourceName));\n                using (var reader = new StreamReader(stream))\n                {\n                    return reader.ReadToEnd();\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error reading embedded SQL script: {ex.Message}");\n            throw;\n        }\n    }\n}