using System;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an XElement with the desired text and entity.\n        var element = new XElement("h1", \n            "Text",\n            new XCData("&nbsp;"),  // Not ideal for HTML output, but demonstrates usage\n            "to",\n            new XCData("&nbsp;"),\n            "keep",\n            new XCData("&nbsp;"),\n            "together.");\n\n        // Alternatively, create a method to handle spaces as &nbsp;\n        var elementWithNbsp = new XElement("h1", KeepTogether("Text to keep together."));\n\n        Console.WriteLine(element);\n        Console.WriteLine(elementWithNbsp);\n    }\n\n    private static XNode KeepTogether(string text)\n    {\n        // Replace spaces with the HTML entity for a non-breaking space\n        string transformedText = text.Replace(" ", "&nbsp;");\n\n        // Return as raw text node without escaping\n        return XElement.Parse($"<xml>{transformedText}</xml>").FirstNode;\n    }\n}