using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string inputFilePath = @"C:\path\to\yourfile.docx";\n        string outputFilePath = @"C:\path\to\output.pdf";\n\n        // Path to soffice.exe (part of LibreOffice installation)\n        string libreOfficePath = @"C:\Program Files\LibreOffice\program\soffice.exe";\n\n        try\n        {\n            var processStartInfo = new ProcessStartInfo(libreOfficePath, $"--headless --invisible --convert-to pdf:writer_pdf_Export {inputFilePath} --outdir {System.IO.Path.GetDirectoryName(outputFilePath)}")\n            {\n                RedirectStandardOutput = true,\n                UseShellExecute = false,\n                CreateNoWindow = true\n            };\n\n            using (Process process = new Process())\n            {\n                process.StartInfo = processStartInfo;\n                process.Start();\n                \n                // Optional: Read the standard output to troubleshoot.\n                string result = process.StandardOutput.ReadToEnd(); \n                process.WaitForExit();\n\n                if (!System.IO.File.Exists(outputFilePath))\n                    throw new Exception("Conversion failed");\n\n                Console.WriteLine("Conversion successful!");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error converting the document: {ex.Message}");\n        }\n    }\n}