using (SPSite site = new SPSite(SPContext.Current.Web.Url))\n{\n    using (SPWeb web = site.OpenWeb())\n    {\n        try\n        {\n            SPList list = web.Lists["Vendors"];\n            SPListItem item = list.Items.Add();\n            item["First Name"] = txtVendorName.Text;\n            item["Last Name"] = txtVLastName.Text;\n            // Assuming 'business' and 'Description' are separate fields\n            item["Business"] = txtDescription.Text; \n            item["Mobile No"] = txtMobileNumber.Text;\n            item["Approved"] = "No";\n            \n            // These fields typically should not be manually set but rather SharePoint handles them automatically\n            // If you want to explicitly set the 'Created By' and 'Modified By', use SPUser objects\n            // Example: item["Created By"] = web.EnsureUser("domain\\username");\n            // However, usually, it's better to let SharePoint handle this\n            \n            item.Update();\n        }\n        catch (Exception ex)\n        {\n            // Log the exception details for debugging\n            SPDiagnosticsService.Local.WriteTrace(0, new SPDiagnosticsCategory("VendorListError", TraceSeverity.Unexpected, EventSeverity.Error), TraceSeverity.Unexpected, "Error adding vendor: {0}", ex.Message);\n            throw; // Rethrow or handle appropriately\n        }\n    }\n}