using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class FileUploader\n{\n    public async Task UploadFileAsync(byte[] fileBytes, string fileName, string url)\n    {\n        using (var client = new HttpClient())\n        using (var content = new MultipartFormDataContent())\n        {\n            // Add the byte array as a stream content part with the appropriate headers\n            var fileContent = new ByteArrayContent(fileBytes);\n            fileContent.Headers.ContentType = System.Net.Http.Headers.MediaTypeHeaderValue.Parse("application/octet-stream");\n            content.Add(fileContent, "content", fileName);\n\n            // Add the other form fields\n            content.Add(new StringContent(fileName), "filename");\n            content.Add(new StringContent("[REMOTE SERVER SAVE URL]"), "saveurl");\n            content.Add(new StringContent("12345678"), "id");\n            content.Add(new StringContent("doc"), "format");\n\n            try\n            {\n                var response = await client.PostAsync(url, content);\n                response.EnsureSuccessStatusCode();\n                Console.WriteLine("File uploaded successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error uploading file: {ex.Message}");\n            }\n        }\n    }\n\n    public static async Task Main(string[] args)\n    {\n        var uploader = new FileUploader();\n        byte[] fileBytes = // Get your byte array here;\n        string fileName = "mydocument.doc";\n        string url = "http://export.writer.zoho.com/remotedoc.im?apikey=[apikey]&output=editor";\n\n        await uploader.UploadFileAsync(fileBytes, fileName, url);\n    }\n}