using System;\nusing System.Collections.Generic;\nusing System.Data.OleDb;\nusing System.Drawing;\nusing System.IO;\n\nclass Program\n{\n    const string connectionString = "Your connection string here";\n    static void Main()\n    {\n        var dataTable = FetchDataFromAccess();\n        GenerateImagesAndHTML(dataTable);\n    }\n\n    static DataTable FetchDataFromAccess()\n    {\n        using (OleDbConnection conn = new OleDbConnection(connectionString))\n        {\n            OleDbCommand cmd = new OleDbCommand("SELECT English, Punjabi FROM YourTableName", conn);\n            conn.Open();\n\n            using (DataTable dataTable = new DataTable())\n            {\n                OleDbDataAdapter adapter = new OleDbDataAdapter(cmd);\n                adapter.Fill(dataTable);\n                return dataTable;\n            }\n        }\n    }\n\n    static void GenerateImagesAndHTML(DataTable table)\n    {\n        int imageCounter = 0;\n        using (StreamWriter writer = new StreamWriter("output.html"))\n        {\n            writer.WriteLine("<html><head><title>Ebook</title></head><body>");\n            writer.WriteLine("<table border='1'>");\n\n            foreach (DataRow row in table.Rows)\n            {\n                string englishWord = row[0].ToString();\n                string punjabiWord = row[1].ToString();\n\n                // Create image for Punjabi word\n                string imageName = $"image{++imageCounter}.png";\n                CreateImageFromText(punjabiWord, imageName);\n\n                writer.WriteLine($"<tr><td>{englishWord}</td><td><img src='{imageName}' /></td></tr>");\n            }\n\n            writer.WriteLine("</table></body></html>");\n        }\n    }\n\n    static void CreateImageFromText(string text, string filename)\n    {\n        using (Bitmap bitmap = new Bitmap(200, 100))\n        using (Graphics g = Graphics.FromImage(bitmap))\n        {\n            Font font = new Font("Arial Unicode MS", 20);\n            \n            // Adjust layout and sizes as needed\n            g.Clear(Color.White);\n            SizeF size = g.MeasureString(text, font);\n            bitmap.SetResolution(100, 100);\n\n            using (Brush brush = new SolidBrush(Color.Black))\n                g.DrawString(text, font, brush, new PointF((200 - size.Width) / 2, (100 - size.Height) / 2));\n            \n            bitmap.Save(filename);\n        }\n    }\n}