internal class MVCDemoRegistry : Registry\n{\n    public MVCDemoRegistry()\n    {\n        Scan(x =>\n        {\n            x.Assembly("MVCDemo");\n            x.Assembly("MVCDemo.Infrastructure");\n            x.Assembly("MVCDemo.Services");\n\n            // Registering generic interfaces\n            x.ConnectImplementationsToTypesClosing(typeof(IGenericInterface<>)).OnAddedPluginTypes(t => t.LifecycleIs(new TransientLifecycle()));\n\n            // If you have a specific non-generic interface like ISupplyView\n            x.AddAllTypesOf<ISupplyView>();\n        });\n    }\n}