// Example of handling MigrateAnonymous event in ASP.NET Web Forms\n   void Session_Start(object sender, EventArgs e)\n   {\n       string anonId = Request.AnonymousID;\n       if (anonId != null && User.Identity.IsAuthenticated)\n       {\n           // Logic to migrate anonymous session data to authenticated session\n           \n           // Clear the anonymous cookie\n           HttpCookie anonCookie = new HttpCookie("ASP.NET_SessionId", "")\n           {\n               Expires = DateTime.Now.AddYears(-1),\n           };\n           Response.Cookies.Add(anonCookie);\n       }\n   }