// Example pseudo-code for configuring symbol options\nvoid ConfigureSymbolOptions()\n{\n    // This represents setting paths and options via VS Tools > Options in C# way\n    DebuggingSymbolsOptions symbolsOptions = new DebuggingSymbolsOptions();\n    symbolsOptions.SymbolSearchPaths.Add("C:\\Path\\To\\PDBs");\n    symbolsOptions.EnableSourceServerSupport = true;\n    \n    DebuggingGeneralOptions generalOptions = new DebuggingGeneralOptions();\n    generalOptions.EnableJustMyCode = false;\n    generalOptions.EnableDotNetFrameworkSourceStepping = true;\n}