using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string originalDbPath = @"C:\path\to\your\databse.accdb";\n        string compactedDbPath = @"C:\path\to\your\compacted_database.accdb";\n\n        // VBA script to compact and repair\n        string vbaScriptPath = @"C:\path\to\CompactDatabase.vbs";\n        \n        // Write the VBA script content to a file\n        System.IO.File.WriteAllText(vbaScriptPath, @"\n        Sub CompactDB()\n            DBEngine.CompactDatabase """ + originalDbPath + @""", """ + compactedDbPath + @"""\n            Application.Quit acQuitSaveNone\n        End Sub");\n\n        ProcessStartInfo psi = new ProcessStartInfo("msaccess.exe")\n        {\n            Arguments = "/x CompactDB " + '"' + vbaScriptPath + '"',\n            CreateNoWindow = true,\n            WindowStyle = ProcessWindowStyle.Hidden\n        };\n        \n        // Running the VBA script\n        Process.Start(psi);\n\n        Console.WriteLine("Database compacted successfully.");\n    }\n}