using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\npublic class VendorDTO\n{\n    public string VendorNo { get; set; }\n    public string VendorName { get; set; }\n}\n\n[WebMethod]\npublic List<VendorDTO> GetVendors(string database)\n{\n    if (string.IsNullOrEmpty(database))\n        throw new ArgumentException("Database parameter cannot be null or empty.");\n\n    var vendors = new List<VendorDTO>();\n    using (SqlConnection sqlConn = new SqlConnection(GetConnString(database)))\n    {\n        string strSQL = @"\n            SELECT [No_] AS [VendorNo], LTRIM([Name]) AS [VendorName]\n            FROM [********_$Vendor]\n            WHERE LEN(RTRIM([Name])) > 0 AND [Vendor Posting Group] = @PostingGroup\n            ORDER BY LTRIM([Name]) ASC;";\n\n        using (SqlCommand cmd = new SqlCommand(strSQL, sqlConn))\n        {\n            cmd.Parameters.AddWithValue("@PostingGroup", "VEND");\n            sqlConn.Open();\n            using (var reader = cmd.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    vendors.Add(new VendorDTO\n                    {\n                        VendorNo = reader["VendorNo"].ToString(),\n                        VendorName = reader["VendorName"].ToString()\n                    });\n                }\n            }\n        }\n    }\n\n    return vendors;\n}