using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string idsList = "1,2,3"; // This is the comma-separated list of IDs\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand("GetItemsByIds", connection);\n            command.CommandType = CommandType.StoredProcedure;\n\n            SqlParameter parameter = new SqlParameter("@myList", SqlDbType.NVarChar, -1); // Use -1 for MAX\n            parameter.Value = idsList;\n            command.Parameters.Add(parameter);\n\n            connection.Open();\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    // Process the results\n                }\n            }\n        }\n    }\n}