using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string directoryPath = @"C:\wallpapers";\n        \n        // Get all files in the directory\n        string[] files = Directory.GetFiles(directoryPath);\n\n        if (files.Length == 0)\n        {\n            Console.WriteLine("No files found.");\n            return;\n        }\n\n        Random random = new Random();\n        // Select a random file\n        int index = random.Next(files.Length);\n        string selectedFile = files[index];\n\n        Console.WriteLine($"Selected wallpaper: {selectedFile}");\n\n        // You can implement a timer to run this logic every 15 minutes\n    }\n}