protected void MyGridView_Command(Object sender, CommandEventArgs e)\n{\n    if (e.CommandName == "ClickButton")\n    {\n        // Convert the row index stored in the CommandArgument property to an integer.\n        int rowIndex = Convert.ToInt32(e.CommandArgument);\n        \n        // Use rowIndex as needed. For example:\n        GridViewRow row = MyGridView.Rows[rowIndex];\n        // Perform your action here using the row.\n    }\n}